import React, { Component } from 'react';

import './App.css';
import {isTSIndexedAccessType} from "@babel/types";

class App extends Component {
    constructor(props){
        super(props);
        this.state={
            noteText: '',
        }
    }



    render() {
        return (
          <div className="container">
                <div className="header">React To-Do Application</div>

                <div className="btn">+</div>

                <input type="text"
                    ref={((input)=>{this.textInput = input})}
                    className="textInput"
                       value={this.state.noteText}
                       onChangeText={noteText => this.updateNoteText(noteText)}
                />
          </div>
        );
    }
}

export default App;
